select ps.period_id, tt.time_stamp, ob.subject_id, s.security_name, ob.offer_type, sp.price_level, tt.units_changed, ob.id, ob.offer_status from period_securities ps, securities s, offer_book ob, security_pricelevels sp, ticker_tape tt where ps.session_id=5 and ps.id=sp.period_security_id and tt.offer_book_id=ob.id and ob.pricelevel_id=sp.id and  ps.security_id=s.id and ob.offer_status='canceled' order by ps.period_id, tt.time_stamp;