select ps.period_id, ob.time_entry, ob.subject_id, ob.id, s.security_name, ob.offer_type, sp.price_level, ob.offer_units, ob.entry_type, ob.offer_status from securities s, offer_book ob, security_pricelevels sp, period_securities ps where ps.session_id=8 and ps.security_id=s.id and ps.id=sp.period_security_id and sp.id=ob.pricelevel_id and ob.entry_type='market' order by ps.period_id, ob.time_entry;