select ps.period_id, tb.id, tb.time_entry, ob.subject_id, ob.id, ob.entry_type, ob.offer_type, s.security_name, sp.price_level, ts.units_contributed, p.market_type from transaction_book tb, transaction_sides ts, securities s, offer_book ob, security_pricelevels sp, period_securities ps, periods p where p.session_id=5 and ps.session_id=5 and p.period_id=ps.period_id and tb.id=ts.transaction_id and ts.offer_id=ob.id and ob.pricelevel_id=sp.id and sp.period_security_id=ps.id and ps.security_id=s.id order by ps.period_id, tb.time_entry, tb.id