select s.security_name, avg(tb.price) from periods p, period_securities ps, securities s, security_pricelevels sl, transaction_book tb, transaction_sides ts, offer_book ob where p.session_id=2 and p.period_id=0 and s.security_name='MS' and ps.session_id=p.session_id and ps.period_id=p.period_id and security_id=s.id and sl.period_security_id=ps.id and sl.id=ob.pricelevel_id and ob.id=ts.offer_id and ts.transaction_id=tb.id group by s.security_name; 